// Function to preload images
function preloadImages(cacheArray) {
    cacheArray.forEach(cachedFile => {
        const img = new Image(); // Create a new Image object
        img.src = `${cachedFile.url}${cachedFile.src}.jpg`; // Construct the URL
    });
}

// Preload images on page load
window.onload = function() {
    preloadImages(cache);
};